package wk1_java_basics;

public class MathProblem2 {
	
	/*
	In wk1_java_basics, add class MathProblem
	Check the box for including a main method
	Declare 3 int variables (vars): x, y, and z
	Set literal values to x and y (hardcoding)
	Assign the sum of x and y to z
	Print the equation
	*/

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Declare Variables (vars) 
		int x;
		int y;
		int z;
		
		//Initiailize Values
		x = 2; 
		y = 3; 
		z = x + y; 
		
		// Print the equation
		System.out.println(x + " + " + y + " = " + z); 

	}

}
